"use strict";

var proto = require("utils/proto");

function Stack() {
	this.list = [];
	this.index = -1;
}

proto(Stack, function push(val) {
	this.index += 1;
	this.list[this.index] = val;

}, function pop() {
	this.index -= 1;

	return this.list.pop();

}, function empty() {
	return (this.index < 0);

}, function top() {
	return this.list[this.index];
});

exports.create = function () {
	return new Stack();
};
